    public void validate(Client c) {
        Pattern pattern = Pattern.compile(EMAIL_PATTERN);
        if (!pattern.matcher(c.getEmail()).matches()) {
            throw new IllegalArgumentException("Email-ul nu este valid!");
        }
        ClientBLL clientBLL = new ClientBLL();
        List<Client> clienti = clientBLL.cautareClienti();
        for (Client client : clienti) {
            if (client.getEmail().equals(c.getEmail())) {
                throw new IllegalArgumentException("Email-ul nu este valid!");

            }
        }
    }
